import React, { useState } from "react";

const Ticket = (props) => (

    <tr>
        <td>{props.ticket.subject}</td>
        <td>{props.ticket.date}</td>
        <td>{props.ticket.status}</td>
    </tr>

)

export default function Lookup() {

    const [email, setEmail] = useState('');
    const [tickets, setTickets] = useState([]);

    async function onSubmit(e) {
        e.preventDefault();

        if (email) {
            console.log(JSON.stringify(email));
            const response = await fetch(`http://localhost:5050/ticket/lookup`, {
                method: "POST",
                body: {email: JSON.stringify(email)},
            });
            const ticketsFromEmail = await response.json();
            setTickets(ticketsFromEmail);
        }
        else{
            alert('email is not set');
        }

    }

    function updateEmail(value) {
        return setEmail(value);
    }

    function ticketList() {
        return tickets.map((ticket) => {
            return (
                <Ticket
                    ticket={ticket}
                />
            )
        })
    }

    return (
        <>

            <h3>{email}</h3>

            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="email"
                        onChange={(e) => updateEmail(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <input
                        type="submit"
                        value="Lookup Email"
                        className="btn btn-primary"
                    />
                </div>
            </form>

            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Subject</th>
                        <th>Date</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>{ticketList()}</tbody>
            </table>
        </>
    );

}
