import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Table, Collapse } from "react-bootstrap";

const Record = (props) => {

  const [expanded, setExpand] = useState(false);

  const expandCol = () => {
    setExpand(!expanded);
  }

  return(
  <>
    <tr onClick={expandCol}>
      <td>{props.record.subject}</td>
      <td>{props.record.date}</td>
      <td>
        <Link className="btn btn-link" to={`/respond/${props.record._id}`}>Respond</Link>
      </td>
    </tr>
    {expanded &&
      <>
          <div>
            {props.record.description}
          </div>
      </>
    }


  </>
  );


};








export default function TicketList() {
  const [records, setRecords] = useState([]);

  // This method fetches the records from the database.
  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5050/ticket/`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const records = await response.json();
      setRecords(records);
    }

    getRecords();

    return;
    //Executes whenever record length changes
  }, [records.length]);

  // This method will delete a record
  async function deleteRecord(id) {
    await fetch(`http://localhost:5050/record/${id}`, {
      method: "DELETE"
    });

    const newRecords = records.filter((el) => el._id !== id);
    setRecords(newRecords);
  }

  // This method will map out the records on the table
  function recordList() {
    return records.map((record) => {
      return (
        <Record
          record={record}
          deleteRecord={() => deleteRecord(record._id)}
          key={record._id}
        />
      );
    });
  }

  // This following section will display the table with the records of individuals.
  return (
    <div>
      <h3>Record List</h3>
      <Table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Subject</th>
            <th>Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{recordList()}</tbody>
      </Table>
    </div>
  );
}
