import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import Dropdown from 'react-bootstrap/Dropdown';

export default function Respond() {
  const [form, setForm] = useState({
    subject: '',
    email: '',
    date: '',
    description: '',
    response: '',
    status: '',

  });
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(`http://localhost:5050/ticket/${params.id.toString()}`);

      if (!response.ok) {
        const message = `An error has occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
    }

    fetchData();

    return;
  }, [params.id, navigate]);

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  async function onSubmit(e) {
    e.preventDefault();
    const updatedTicket = {
      subject: form.subject,
      email: form.email,
      date: form.date,
      description: form.description,
      response: form.response,
      status: form.status,
    };

    // This will send a post request to update the data in the database.
    await fetch(`http://localhost:5050/ticket/${params.id}`, {
      method: "PATCH",
      body: JSON.stringify(updatedTicket),
      headers: {
        'Content-Type': 'application/json'
      },
    });

    navigate("/");
  }

  // This following section will display the form that takes input from the user to update the data.
  return (
    <div>
      <h3>Respond to Ticket</h3>
      <div>
        Ticket Information:
        <br />
        Subject: {form.subject}
        <br />
        email: {form.email}
        <br />
        Description: {form.description}
        <br />
        status: {form.status}

      </div>

      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="description">Enter response here:</label>
          <textarea
            type="text"
            className="form-control"
            id="response"
            value={form.response}
            onChange={(e) => updateForm({ response: e.target.value })}
          ></textarea>
        </div>

        <div className="form-group">
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="statusOptions"
              id="statusActive"
              value="Active"
              checked={form.status === "Active"}
              onChange={(e) => updateForm({ status: e.target.value })}
            />
            <label htmlFor="positionJunior" className="form-check-label">Active</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="statusOptions"
              id="statusPending"
              value="Pending"
              checked={form.status === "Pending"}
              onChange={(e) => updateForm({ status: e.target.value })}
            />
            <label htmlFor="statusPending" className="form-check-label">Pending Response</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="statusOptions"
              id="positionResolved"
              value="Resolved"
              checked={form.status === "Resolved"}
              onChange={(e) => updateForm({ status: e.target.value })}
            />
            <label htmlFor="positionSenior" className="form-check-label">Resolved</label>
          </div>
        </div>

        <Dropdown>
          <Dropdown.Toggle>
            Dropdown
          </Dropdown.Toggle>
        </Dropdown>

        <div className="form-group">
          <input
            type="submit"
            value="Submit Response"
            className="btn btn-primary"
          />
        </div>

      </form>
    </div>
  );
}
