import React from "react";

// We import bootstrap to make our application look better.
import "bootstrap/dist/css/bootstrap.css";

// We import NavLink to utilize the react router.
import { NavLink } from "react-router-dom";
import Container from "react-bootstrap/Container";
import { NavDropdown } from "react-bootstrap";
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { useNavigate } from "react-router";

// Here, we display our Navbar
export default function Topbar({ isAdmin, logout }) {

  const navigate = useNavigate();

  return (
    <Navbar expand="lg" className="bg-body-tertiary">
      <Container>
        <NavLink className="navbar-brand" to="/">
          <img alt="React Logo" style={{ "width": 25 + '%' }} src="/logo192.png"></img>
        </NavLink>

        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <NavLink className="nav-link" to="/">
              Dashboard
            </NavLink>
            <NavLink className="nav-link" to="/create">
              Create
            </NavLink>
            <NavLink className="nav-link" to="/lookup">
              Lookup
            </NavLink>
            <NavLink className="nav-link" to="/login">
              Login
            </NavLink>
            {isAdmin && <NavDropdown title="Admin" id="basic-nav-dropdown">
              <NavDropdown.Item>
                <NavLink className="nav-link" to="/ticketList">
                  Respond to Tickets
                </NavLink>
              </NavDropdown.Item>

              <NavDropdown.Item>
                <NavLink className="nav-link" onClick={logout} to="/">
                  Logout
                </NavLink>
              </NavDropdown.Item>
            </NavDropdown>}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
