import React, { useState } from "react";

const Ticket = (props) => {
    const [expanded, setExpand] = useState(false);

    const expandCol = () => {
        setExpand(!expanded);
    }

    return(
    <>
        <tr onClick={expandCol}>
            <td>{props.ticket.subject}</td>
            <td>{props.ticket.date}</td>
            <td>{props.ticket.status}</td>
        </tr>

        {expanded && 
            <tr>
                <td>
                    <div>
                        Email: {props.ticket.email}
                        <br/>
                        Description: {props.ticket.description}
                        <br/>
                        Response: {props.ticket.response}
                    </div>
                </td>
            </tr>
        }
    </>
    );

};






export default function Lookup() {

    const [email, setEmail] = useState();
    const [tickets, setTickets] = useState([]);

    async function onSubmit(e) {
        e.preventDefault();

        if (email) {
            const response = await fetch(`http://localhost:5050/ticket/lookup/${email}`);

            const ticketsFromEmail = await response.json();
            setTickets(ticketsFromEmail);
        }
        else {
            alert('email is not set');
        }

    }

    function updateEmail(value) {
        return setEmail(value);
    }

    function ticketList() {
        return tickets.map((ticket) => {
            return (
                <Ticket
                    ticket={ticket}
                />
            )
        })
    }

    return (
        <>

            <h3>{email}</h3>

            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="email"
                        value={email}
                        onChange={(e) => updateEmail(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <input
                        type="submit"
                        value="Lookup Email"
                        className="btn btn-primary"
                    />
                </div>
            </form>

            <table className="table table-striped" style={{ marginTop: 20 }}>
                <thead>
                    <tr>
                        <th>Subject</th>
                        <th>Date</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>{ticketList()}</tbody>
            </table>
        </>
    );

}
